import React, { useState, useEffect } from 'react';
import './styles/App.css';
import axios from 'axios';

const Home = () => <span>🏠</span>;
const UserIcon = () => <span>👤</span>;
const MessageCircle = () => <span>💬</span>;
const Settings = () => <span>⚙️</span>;
const LogOut = () => <span>🚪</span>;
const LogIn = () => <span>🔑</span>;
const UserPlus = () => <span>➕</span>;
const LayoutDashboard = () => <span>📊</span>;
const AlertCircle = () => <span>⚠️</span>;
const CheckCircle = () => <span>✅</span>;
const Loader2 = () => <div className="loader"></div>;

const App = () => {
    const [activePage, setActivePage] = useState('home');
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [notifications, setNotifications] = useState([]);
    const [mentors, setMentors] = useState([]);
    const [mentorsLoading, setMentorsLoading] = useState(false);
    const [mentorsError, setMentorsError] = useState(null);
    const [mentees, setMentees] = useState([]);
    const [menteesLoading, setMenteesLoading] = useState(false);
    const [menteesError, setMenteesError] = useState(null);

    const handleLogin = () => setIsLoggedIn(true);
    const handleLogout = () => setIsLoggedIn(false);

    const fetchMentors = () => {
        setMentorsLoading(true);
        axios.get('http://localhost:8080/api/mentors')
            .then(response => {
                setMentors(response.data);
                setMentorsLoading(false);
            })
            .catch(error => {
                setMentorsError('Failed to load mentors.');
                setMentorsLoading(false);
                console.error('Error loading mentors:', error);
            });
    };

    const fetchMentees = () => {
        setMenteesLoading(true);
        axios.get('http://localhost:8080/api/mentees')
            .then(response => {
                setMentees(response.data);
                setMenteesLoading(false);
            })
            .catch(error => {
                setMenteesError('Failed to load mentees.');
                setMenteesLoading(false);
                console.error('Error loading mentees:', error);
            });
    };

    const markNotificationAsRead = (id) => {
        setNotifications(notifications.map(n => n.id === id ? { ...n, read: true } : n));
    };

    const markAllNotificationsAsRead = () => {
        setNotifications(notifications.map(n => ({ ...n, read: true })));
    };

    return (
        <div className="app">
            <aside className="sidebar">
                <div>
                    <h1>MentorConnect</h1>
                    <nav>
                        <button
                            className={activePage === 'home' ? 'active' : ''}
                            onClick={() => setActivePage('home')}
                        >
                            <Home /> Home
                        </button>
                        <button
                            className={activePage === 'mentors' ? 'active' : ''}
                            onClick={() => {
                                setActivePage('mentors');
                                if (mentors.length === 0 && !mentorsLoading && !mentorsError) {
                                    fetchMentors();
                                }
                            }}
                        >
                            <UserIcon /> Mentors
                        </button>
                        <button
                            className={activePage === 'mentees' ? 'active' : ''}
                            onClick={() => {
                                setActivePage('mentees');
                                if (mentees.length === 0 && !menteesLoading && !menteesError) {
                                    fetchMentees();
                                }
                            }}
                        >
                            <UserPlus /> Mentees
                        </button>
                        <button
                            className={activePage === 'profile' ? 'active' : ''}
                            onClick={() => setActivePage('profile')}
                        >
                            <UserIcon /> Profile
                        </button>
                        <button
                            className={activePage === 'dashboard' ? 'active' : ''}
                            onClick={() => setActivePage('dashboard')}
                        >
                            <LayoutDashboard /> Dashboard
                        </button>
                        <button
                            className={activePage === 'messages' ? 'active' : ''}
                            onClick={() => setActivePage('messages')}
                        >
                            <MessageCircle /> Messages
                            {/* {unreadCount > 0 && (
                                <span className="badge">{unreadCount}</span>
                            )} */}
                        </button>
                        {isLoggedIn && (
                            <button
                                className={activePage === 'admin' ? 'active' : ''}
                                onClick={() => setActivePage('admin')}
                            >
                                <Settings /> Admin Panel
                            </button>
                        )}
                    </nav>
                </div>
                <div className="auth-buttons">
                    {isLoggedIn ? (
                        <button onClick={handleLogout}>
                            <LogOut /> Logout
                        </button>
                    ) : (
                        <>
                            <button onClick={handleLogin}>
                                <LogIn /> Login
                            </button>
                            <button className="secondary">
                                <UserPlus /> Signup
                            </button>
                        </>
                    )}
                </div>
            </aside>
            <main className="main">
                <div className="page-content">
                    {activePage === 'home' && (
                        <div>
                            <h2>Welcome to MentorConnect!</h2>
                            <p>This is your personalized mentoring platform.</p>
                            {isLoggedIn && (
                                <div className="notifications">
                                    <h3>Your Notifications</h3>
                                    {notifications.length === 0 ? (
                                        <p>No notifications.</p>
                                    ) : (
                                        <div>
                                            {notifications.filter(n => !n.read).length > 0 && (
                                                <button className="mark-all" onClick={markAllNotificationsAsRead}>
                                                    Mark all as read
                                                </button>
                                            )}
                                            {notifications.map((notification) => (
                                                <div
                                                    key={notification.id}
                                                    className={`notification ${notification.read ? 'read' : 'unread'}`}
                                                >
                                                    <div>
                                                        <p>
                                                            {notification.sender} {notification.text}
                                                        </p>
                                                        <p className="notification-time">
                                                            {new Date(notification.timestamp).toLocaleTimeString()}
                                                        </p>
                                                    </div>
                                                    {!notification.read && (
                                                        <button
                                                            className="mark-read"
                                                            onClick={() => markNotificationAsRead(notification.id)}
                                                        >
                                                            <CheckCircle />
                                                        </button>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                    {activePage === 'mentors' && (
                        <div className="fade-in">
                            <h2>Mentors</h2>
                            {mentorsLoading && <div>Loading mentors...</div>}
                            {mentorsError && <div>Error: {mentorsError}</div>}
                            {!mentorsLoading && !mentorsError && mentors.length > 0 && (
                                <ul>
                                    {mentors.map(mentor => (
                                        <li key={mentor.id}>{mentor.name}</li>
                                    ))}
                                </ul>
                            )}
                            {!mentorsLoading && !mentorsError && mentors.length === 0 && (
                                <p>No mentors found.</p>
                            )}
                        </div>
                    )}
                    {activePage === 'mentees' && (
                        <div className="fade-in">
                            <h2>Mentees</h2>
                            {menteesLoading && <div>Loading mentees...</div>}
                            {menteesError && <div>Error: {menteesError}</div>}
                            {!menteesLoading && !menteesError && mentees.length > 0 && (
                                <ul>
                                    {mentees.map(mentee => (
                                        <li key={mentee.id}>{mentee.name}</li> 
                                    ))}
                                </ul>
                            )}
                            {!menteesLoading && !menteesError && mentees.length === 0 && (
                                <p>No mentees found.</p>
                            )}
                        </div>
                    )}
                    {/* {activePage === 'profile' && <Profile />}
                    {activePage === 'dashboard' && <Dashboard />}
                    {activePage === 'messages' && <Messages />}
                    {activePage === 'admin' && <AdminPanel />} */}
                </div>
            </main>
        </div>
    );
};

export default App;
