import React, { useState } from 'react';
import './styles/App.css';

// Mock icons (you can replace these with actual icons or images)
const Home = () => <span>🏠</span>;
const UserIcon = () => <span>👤</span>;
const MessageCircle = () => <span>💬</span>;
const Settings = () => <span>⚙️</span>;
const LogOut = () => <span>🚪</span>;
const CheckCircle = () => <span>✅</span>;

// Mock notifications data (updated to match the image)
const mockNotifications = [
    { id: 'n1', type: 'message', sender: 'Alloe', text: 'sent you a mosacge', timestamp: '2025-05-14T14:30:00Z', read: false },
    { id: 'n2', type: 'request', sender: 'Bob', text: 'sent you a mentor request', timestamp: '2025-05-14T13:30:00Z', read: true },
    { id: 'n3', type: 'update', sender: 'System', text: 'Your priflle has been bpdated', timestamp: '2025-05-14T12:30:00Z', read: true },
];

const App = () => {
    const [activePage, setActivePage] = useState('home');
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [notifications, setNotifications] = useState(mockNotifications);

    const unreadCount = notifications.filter(n => !n.read).length;

    const handleLogout = () => setIsLoggedIn(false);

    const markNotificationAsRead = (id) => {
        setNotifications(notifications.map(n => n.id === id ? { ...n, read: true } : n));
    };

    const markAllNotificationsAsRead = () => {
        setNotifications(notifications.map(n => ({ ...n, read: true })));
    };

    return (
        <div className="app">
            <aside className="sidebar">
                <div>
                    <h1>MentorConnect</h1>
                    <nav>
                        <button
                            className={activePage === 'home' ? 'active' : ''}
                            onClick={() => setActivePage('home')}
                        >
                            <Home /> Home
                        </button>
                        <button
                            className={activePage === 'profile' ? 'active' : ''}
                            onClick={() => setActivePage('profile')}
                        >
                            <UserIcon /> Profile
                        </button>
                        <button
                            className={activePage === 'dashboard' ? 'active' : ''}
                            onClick={() => setActivePage('dashboard')}
                        >
                            <UserIcon /> Dashboard
                        </button>
                        <button
                            className={activePage === 'messages' ? 'active' : ''}
                            onClick={() => setActivePage('messages')}
                        >
                            <MessageCircle /> Messages
                            {unreadCount > 0 && (
                                <span className="badge">{unreadCount}</span>
                            )}
                        </button>
                        {isLoggedIn && (
                            <button
                                className={activePage === 'admin' ? 'active' : ''}
                                onClick={() => setActivePage('admin')}
                            >
                                <Settings /> Admin Panel
                            </button>
                        )}
                    </nav>
                </div>
                <div className="auth-buttons">
                    {isLoggedIn ? (
                        <button onClick={handleLogout}>
                            <LogOut /> Logout
                        </button>
                    ) : null}
                </div>
            </aside>
            <main className="main">
                <div className="page-content">
                    {activePage === 'home' && (
                        <div className="fade-in">
                            <h2>Welcome to MentorConnect!</h2>
                            <h3>Hattle your personallized Mittform.</h3>
                            <p>Welcome to MentorConnect! Thits is your personallized mentorig pltform.</p>
                            {isLoggedIn && (
                                <div className="notifications">
                                    <h3>Your Notifications</h3>
                                    {notifications.length === 0 ? (
                                        <p>No notifications.</p>
                                    ) : (
                                        <div>
                                            {notifications.filter(n => !n.read).length > 0 && (
                                                <button className="mark-all" onClick={markAllNotificationsAsRead}>
                                                    Mark all as read
                                                </button>
                                            )}
                                            {notifications.map((notification, index) => (
                                                <div
                                                    key={notification.id}
                                                    className={`notification ${notification.read ? 'read' : 'unread'} fade-in`}
                                                    style={{ animationDelay: `${index * 0.1}s` }}
                                                >
                                                    <div>
                                                        <p>
                                                            {notification.sender} {notification.text}
                                                        </p>
                                                        <p className="notification-time">
                                                            {new Date(notification.timestamp).toLocaleTimeString()}
                                                        </p>
                                                    </div>
                                                    {!notification.read && (
                                                        <button
                                                            className="mark-read"
                                                            onClick={() => markNotificationAsRead(notification.id)}
                                                        >
                                                            <CheckCircle />
                                                        </button>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                    {/* You can add other pages here if needed */}
                </div>
            </main>
        </div>
    );
};

export default App;
