import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/MenteeList.css';
function MenteeList() {
  const [mentees, setMentees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/mentees')
      .then(response => {
        setMentees(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
        console.error('Error fetching mentees:', error);
      });
  }, []);

  if (loading) {
    return <p>Loading mentees...</p>;
  }

  if (error) {
    return <p>Error loading mentees.</p>;
  }

  return (
    <div className="mentee-list">
      <h2>Looking for Mentors</h2>
      <div className="mentee-container">
        {mentees.map(mentee => (
          <div key={mentee.id} className="mentee-card">
            <h3>{mentee.name}</h3>
            <p><strong>Interested In:</strong> {mentee.interestedIn}</p>
            <p><strong>Bio:</strong> {mentee.bio}</p>
            <p><strong>Contact:</strong> <a href={`mailto:${mentee.contactEmail}`}>{mentee.contactEmail}</a></p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MenteeList;
