import React, { useState, useEffect } from 'react';
import './styles/App.css';
import MentorList from './components/MentorList'; // Import MentorList component
import MenteeList from './components/MenteeList'; // Import MenteeList component
import Profile from './components/Profile';
import Dashboard from './components/Dashboard';
import Messages from './components/Messages';
import AdminPanel from './components/AdminPanel';
import axios from 'axios';

const Home = () => <span>üè†</span>;
const UserIcon = () => <span>üë§</span>;
const MessageCircle = () => <span>üí¨</span>;
const Settings = () => <span>‚öôÔ∏è</span>;
const LogOut = () => <span>üö™</span>;
const LogIn = () => <span>üîë</span>;
const UserPlus = () => <span>‚ûï</span>;
const LayoutDashboard = () => <span>üìä</span>;
const AlertCircle = () => <span>‚ö†Ô∏è</span>;
const CheckCircle = () => <span>‚úÖ</span>;
const Loader2 = () => <div className="loader"></div>;

// Helper Components
const OnlineStatus = ({ isOnline }) => (
    <span className={`online-status ${isOnline ? 'online' : 'offline'}`}></span>
);

const App = () => {
    const [activePage, setActivePage] = useState('home');
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [notifications, setNotifications] = useState(mockNotifications);

    // Mock data (moved inside App component)
    const mockUsers = [
        { id: '1', name: 'Alice Smith', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&1', isOnline: true },
        { id: '2', name: 'Bob Johnson', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&2', isOnline: true },
        { id: '3', name: 'Charlie Brown', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&3', isOnline: false },
        { id: '4', name: 'Diana Miller', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&4', isOnline: true },
        { id: '5', name: 'Ethan Davis', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&5', isOnline: false },
        { id: '6', name: 'Fiona Wilson', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&6', isOnline: true },
        { id: '7', name: 'George Lee', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&7', isOnline: false },
        { id: '8', name: 'Hannah Clark', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&8', isOnline: true },
        { id: '9', name: 'Isaac Martinez', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&9', isOnline: false },
        { id: '10', name: 'Jessica Anderson', avatarUrl: 'https://source.unsplash.com/random/100x100/?portrait&10', isOnline: true },
    ];

    const mockProfile = {
        name: 'John Doe',
        bio: 'Software Engineer | Mentor | Open Source Enthusiast',
        location: 'San Francisco, CA',
        joined: '2021-08-15',
        avatarUrl: 'https://source.unsplash.com/random/150x150/?portrait',
    };

    const mockDashboard = {
        mentorsCount: 25,
        menteesCount: 150,
        messagesCount: 50,
        activeSessions: 10,
    };

    const mockMessages = [
        { id: 'm1', sender: 'Alice', text: 'Hi John!', timestamp: '2024-07-24T10:00:00Z' },
        { id: 'm2', sender: 'John', text: 'Hello Alice!', timestamp: '2024-07-24T10:05:00Z' },
        { id: 'm3', sender: 'Alice', text: 'How are you?', timestamp: '2024-07-24T10:10:00Z' },
    ];

    const mockNotifications = [
        { id: 'n1', type: 'message', sender: 'Alice', text: 'sent you a message', timestamp: '2024-07-24T09:00:00Z', read: false },
        { id: 'n2', type: 'request', sender: 'Bob', text: 'sent you a mentor request', timestamp: '2024-07-24T08:00:00Z', read: true },
        { id: 'n3', type: 'update', sender: 'System', text: 'Your profile has been updated', timestamp: '2024-07-24T07:00:00Z', read: false },
    ];

    const unreadCount = notifications.filter(n => !n.read).length;

    const handleLogin = () => setIsLoggedIn(true);
    const handleLogout = () => setIsLoggedIn(false);

    const markNotificationAsRead = (id) => {
        setNotifications(notifications.map(n => n.id === id ? { ...n, read: true } : n));
    };

    const markAllNotificationsAsRead = () => {
        setNotifications(notifications.map(n => ({ ...n, read: true })));
    };

    return (
        <div className="app">
            <aside className="sidebar">
                <div>
                    <h1>MentorConnect</h1>
                    <nav>
                        <button
                            className={activePage === 'home' ? 'active' : ''}
                            onClick={() => setActivePage('home')}
                        >
                            <Home /> Home
                        </button>
                        <button
                            className={activePage === 'profile' ? 'active' : ''}
                            onClick={() => setActivePage('profile')}
                        >
                            <UserIcon /> Profile
                        </button>
                        <button
                            className={activePage === 'dashboard' ? 'active' : ''}
                            onClick={() => setActivePage('dashboard')}
                        >
                            <LayoutDashboard /> Dashboard
                        </button>
                        <button
                            className={activePage === 'messages' ? 'active' : ''}
                            onClick={() => setActivePage('messages')}
                        >
                            <MessageCircle /> Messages
                            {unreadCount > 0 && (
                                <span className="badge">{unreadCount}</span>
                            )}
                        </button>
                        {isLoggedIn && (
                            <button
                                className={activePage === 'admin' ? 'active' : ''}
                                onClick={() => setActivePage('admin')}
                            >
                                <Settings /> Admin Panel
                            </button>
                        )}
                        <button
                            className={activePage === 'mentors' ? 'active' : ''}
                            onClick={() => setActivePage('mentors')}
                        >
                            <UserIcon /> Mentors
                        </button>
                        <button
                            className={activePage === 'mentees' ? 'active' : ''}
                            onClick={() => setActivePage('mentees')}
                        >
                            <UserPlus /> Mentees
                        </button>
                    </nav>
                </div>
                <div className="auth-buttons">
                    {isLoggedIn ? (
                        <button onClick={handleLogout}>
                            <LogOut /> Logout
                        </button>
                    ) : (
                        <>
                            <button onClick={handleLogin}>
                                <LogIn /> Login
                            </button>
                            <button className="secondary">
                                <UserPlus /> Signup
                            </button>
                        </>
                    )}
                </div>
            </aside>
            <main className="main">
                {activePage === 'home' && (
                    <div>
                        <h2>Welcome to MentorConnect!</h2>
                        <p>This is your personalized mentoring platform.</p>
                        {isLoggedIn && (
                            <div className="notifications">
                                <h3>Your Notifications</h3>
                                {notifications.length === 0 ? (
                                    <p>No notifications.</p>
                                ) : (
                                    <div>
                                        {notifications.filter(n => !n.read).length > 0 && (
                                            <button className="mark-all" onClick={markAllNotificationsAsRead}>
                                                Mark all as read
                                            </button>
                                        )}
                                        {notifications.map((notification) => (
                                            <div
                                                key={notification.id}
                                                className={`notification ${notification.read ? 'read' : 'unread'}`}
                                            >
                                                <div>
                                                    <p>
                                                        {notification.sender} {notification.text}
                                                    </p>
                                                    <p className="notification-time">
                                                        {new Date(notification.timestamp).toLocaleTimeString()}
                                                    </p>
                                                </div>
                                                {!notification.read && (
                                                    <button
                                                        className="mark-read"
                                                        onClick={() => markNotificationAsRead(notification.id)}
                                                    >
                                                        <CheckCircle />
                                                    </button>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                )}
                {activePage === 'profile' && <Profile />}
                {activePage === 'dashboard' && <Dashboard />}
                {activePage === 'messages' && <Messages />}
                {activePage === 'admin' && <AdminPanel />}
                {activePage === 'mentors' && <MentorList />}
                {activePage === 'mentees' && <MenteeList />}
            </main>
        </div>
    );
};

export default App;
