import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/MentorList.css';

function MentorList() {
  const [mentors, setMentors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/mentors')
      .then(response => {
        setMentors(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
        console.error('Error fetching mentors:', error);
      });
  }, []);

  if (loading) {
    return <p>Loading mentors...</p>;
  }

  if (error) {
    return <p>Error loading mentors.</p>;
  }

  return (
    <div className="mentor-list">
      <h2>Available Mentors</h2>
      <div className="mentor-container">
        {mentors.map(mentor => (
          <div key={mentor.id} className="mentor-card">
            <h3>{mentor.name}</h3>
            <p><strong>Expertise:</strong> {mentor.expertise}</p>
            <p><strong>Bio:</strong> {mentor.bio}</p>
            <p><strong>Contact:</strong> <a href={`mailto:${mentor.contactEmail}`}>{mentor.contactEmail}</a></p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MentorList;
